vvv>^v^vv>><^^<<^>>v>v<^^^<^><^^vv>^vv<^<vv<>v><^<><v><^^^>>^<><^<v>>>>v^<v>>>>>v<><^^>v<^<^>><v<>^>vv>^^v^v^<<v<><<<^v^><<^<><<<<v<^>><<<>v>>vv><vv<><<^<^<><vv>^^^^<>v<<<<v>vv<>vv^^^>><>vv^><>>^vv<<><^^vv<>v^>>^<<>^<v^<^>v<' }

  context 'part 1:delivering solo' do

    it 'delivers to two houses' do
      do_deliveries('>')
      expect(@houses.size).to eq 2
    end

    it 'delivers to four houses (including first house twice)' do
      do_deliveries('^>v<')
      expect(@houses.size).to eq 4
      expect(@houses.first.presents).to eq 2
      expect(@houses[1..-1].all? { |h| h.presents == 1 }).to be_truthy
    end

    it 'delivers multiple presents to two houses' do
      do_deliveries('^v^v^v^v^v')
      expect(@houses.size).to eq 2
      expect(@houses.all? { |h| h.presents > 1 }).to be_truthy
    end

    it 'puzzle part 1' do
      do_deliveries(@directions)

      number_of_houses_visited = @houses.size
      expect(number_of_houses_visited).to be > 1
      p "#{number_of_houses_visited} houses have received at leat one present"
    end
  end


  context 'part 2: delivering with Robo-Santa' do

    def split_directions(directions)
      santa_directions      = []
      robo_santa_directions = []

      directions.split(//).each_with_index do |dir, index|
        if index.even?
          santa_directions    << dir
        else
          robo_santa_directions << dir
        end
      end

      [santa_directions.join(''), robo_santa_directions.join('')]
    end

    it 'deliver to three houses' do
      dirs = split_directions('^v')
      do_deliveries(dirs.first)
      do_deliveries(dirs.last, :no_init)
      expect(@houses.uniq.size).to eq 3
    end

    it 'deliver to three houses, with Santas ending up back at the start' do
      dirs = split_directions('^>v<')
      santa_pos = do_deliveries(dirs.first)
      robo_santa_pos = do_deliveries(dirs.last, :no_init)
      expect(@houses.uniq.size).to eq 3
      expect(santa_pos).to eq ({ x: 0, y: 0 })
      expect(robo_santa_pos).to eq ({ x: 0, y: 0 })
    end

    it 'deliver to eleven houses, with Santas going in opposite directions' do
      dirs = split_directions('^v^v^v^v^v')
      santa_pos = do_deliveries(dirs.first)
      robo_santa_pos = do_deliveries(dirs.last, :no_init)

      directions = (santa_pos[:y] / robo_santa_pos[:y]) > 0 ? :same : :opposite
      expect(directions).to eq :opposite
    end

    it 'puzzle part 2' do
      dirs = split_directions(@directions)
      santa_pos = do_deliveries(dirs.first)
      robo_santa_pos = do_deliveries(dirs.last, :no_init)
      expect(@houses.size).to be > 1
      p "#{@houses.size} houses received at least one present"
    end
  end

end
